// src/components/ProfessorMap.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { Icon } from 'leaflet';
import 'leaflet/dist/leaflet.css';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const professors = [
  {
    id: 1,
    name: 'Prof. Dupont',
    location: 'Université de Paris',
    lat: 48.8566,
    lng: 2.3522,
  },
  {
    id: 2,
    name: 'Prof. Martin',
    location: 'Sorbonne',
    lat: 48.8484,
    lng: 2.3456,
  },
];

function ProfessorMap() {
  const customIcon = new Icon({
    iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    shadowSize: [41, 41],
  });

  return (
    <MapContainer style={containerStyle} center={[48.8566, 2.3522]} zoom={12}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {professors.map((professor) => (
        <Marker
          key={professor.id}
          position={[professor.lat, professor.lng]}
          icon={customIcon}
        >
          <Popup>
            <strong>{professor.name}</strong>
            <br />
            {professor.location}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default ProfessorMap;
